// import { formatDistanceToNow } from 'date-fns';
import { formatDistanceToNow } from "date-fns";

const timeAgo = (date) => {
  const result = formatDistanceToNow(new Date(date), { addSuffix: true });
  return result.replace(/^about\s/, ''); 
};

export default timeAgo;
