import React, { useContext } from "react";
import "./Userhome.css";
import { userprofilecontext } from "../../Allcontexts/Getloggedinuser";
import CollectionsOutlinedIcon from "@mui/icons-material/CollectionsOutlined";
import { alltweetscontext } from "../../Allcontexts/Alltweets";

function Userhome() {
  // userprofile context
  const { userprofile } = useContext(userprofilecontext);

  // alltweets are here the context
  const { alltweets } = useContext(alltweetscontext);
  return (
    <>
      <div className="middle-sec-foryoudiv-main">
        <h1>for you</h1>
        <h1>following</h1>
      </div>
      <hr style={{ margin: "0" }} />
      <section className="userhome-main-section">
        <div className="profile-plus-tweet-div">
          <div className="profile-div">
            <img src={userprofile?.profile} alt="" />
          </div>
          <input
            className="tweet-input"
            placeholder="whats happening now 🤷‍♂️🫠"
            type="text"
          />
        </div>
        <div className="icon-plus-post-div">
          <span>
            <CollectionsOutlinedIcon />
          </span>
          <button className="post-tweet-btn">Post</button>
        </div>
        <hr style={{ margin: "0" }} />
      </section>
      {/*  all tweets are here  */}
      <section className="all-tweets-main-sec">
{
  alltweets.map((alltweet , i)=>(
    <div className="mapped-tweets-main-div">
          <div className="profile-plus-all-other"></div>
    </div>
  ))
}
      </section>
    </>
  );
}

export default Userhome;
