              <h1>
                <CommentBankOutlinedIcon />
              </h1>
              <h1>
                <span
                  onClick={() => likedislikefunction(alltweet._id)}
                  className="like-dislike-span"
                >
                  {alltweet?.like?.length}
                </span>
                <ThumbUpOutlinedIcon />
              </h1>
              <h1>
                <BookmarkAddOutlinedIcon />
              </h1>
            </div>
          </div>
        ))}
      </section>
    </>
  );
}

export default Foryou;
