import { formatDistanceToNow } from 'date-fns';
import { parseISO } from 'date-fns';
import { utcToZonedTime } from 'date-fns-tz';

const timeAgo = (date) => {
  // Parse the ISO date string
  const parsedDate = parseISO(date);

  // Convert the parsed date to UTC time
  const zonedDate = utcToZonedTime(parsedDate, 'UTC');

  // Log the parsed and zoned date for debugging
  console.log('Parsed Date:', parsedDate.toISOString());
  console.log('Zoned Date (UTC):', zonedDate.toISOString());

  // Get the distance to now from the UTC zoned date
  const result = formatDistanceToNow(zonedDate, { addSuffix: true });

  // Log the result for debugging
  console.log('Formatted result:', result);

  return result.replace(/^about\s/, '');
};

export default timeAgo;
