import React, { createContext, useState, useEffect } from "react";

export const manageseperatestatecontext = createContext();

const Takeseperatestateprovider = ({ children }) => {
  const [seperatestae, setseperatestae] = useState(null);
  console.log(seperatestae);

  //   set-in local staorage
  useEffect(() => {
    if (seperatestae !== null) {
      localStorage.setItem("seperatestate", JSON.stringify(seperatestae));
    }
  }, [seperatestae]);

  //   get from local storage
  useEffect(() => {
    const takeseperatestate = localStorage.getItem("Userinformation");
    if (takeseperatestate) {
      setseperatestae(JSON.parse(takeseperatestate));
    }
  }, []);

  const takeseperatemanagestate = (take) => {
    setseperatestae(take);
  };
  return (
    <>
      <manageseperatestatecontext.Provider
        value={{ userinformationdata, takeseperatemanagestate }}
      >
        {children}
      </manageseperatestatecontext.Provider>
    </>
  );
};

export default Takeseperatestateprovider;
