// import { formatDistanceToNow } from 'date-fns';
const timeAgo = (date) => {
  const parsedDate = new Date(date);
  console.log('Parsed Date:', parsedDate);
  const result = formatDistanceToNow(parsedDate, { addSuffix: true });
  return result.replace(/^about\s/, ''); 
};
