import CollectionsOutlinedIcon from '@mui/icons-material/CollectionsOutlined';
import { alltweetscontext } from "../../Allcontexts/Alltweets";

function Userhome() {
  // userprofile context
  const { userprofile } = useContext(userprofilecontext);

