import { formatDistanceToNow } from "date-fns";

const timeAgo = (date) => {
  // Manually parse the date to ensure it's treated as UTC
  const parsedDate = new Date(date);
  
  // Log the parsed date for debugging
  console.log('Parsed Date:', parsedDate.toUTCString()); // Ensuring it's logged as UTC
  
  const result = formatDistanceToNow(parsedDate, { addSuffix: true });
  
  // Log the result for debugging
  console.log('Formatted result:', result);
  
  return result.replace(/^about\s/, '');
};

export default timeAgo;
