import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import "./Register.css";
import xlogo from "../../assets/xlogo.png";
import axios from "axios";

function Register() {
  const [name, setname] = useState("");
  const [username, setusername] = useState("");
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");

  // registerClick
  const registerClick = async (e) => {
    e.preventDefault();
    const userregister = axios.post(`http://localhost:3000/api/auth/register`, {
      name,
      username,
      email,
      password,
    });

    console.log(userregister.object);
  };
  return (
    <>
      <div className="reg-section">
        <img className="x-img" src={xlogo} alt="" />

        <form className="reg-form" onSubmit={registerClick}>
          <input
            onChange={(e) => {
              setname(e.target.value);
            }}
            placeholder="name"
            className="reg-input"
            type="text"
          />
          <input
            onChange={(e) => {
              setusername(e.target.value);
            }}
            placeholder="username"
            className="reg-input"
            type="text"
          />
          <input
            onChange={(e) => {
              setemail(e.target.value);
            }}
            placeholder="email"
            className="reg-input"
            type="email"
          />
          <input
            onChange={(e) => {
              setpassword(e.target.value);
            }}
            placeholder="password"
            className="reg-input"
            type="password"
          />
          <div className="reg-btn-div">
            <Button type="submit" className="reg-btn" variant="success">
              Register ..
            </Button>
            <Button className="login-btn" variant="danger">
              Login ..
            </Button>
          </div>
        </form>
      </div>
    </>
  );
}

export default Register;
