import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import "./Register.css";
import xlogo from "../../assets/xlogo.png";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const navigate = useNavigate();

  // register - Click
  const registerClick = async (e) => {
    e.preventDefault();

    try {
      const userregister = await axios.post(
        `http://localhost:3000/api/auth/login`,
        {
          email,
          password,
        }
      );

      console.log(userregister);
    } catch (error) {
     console.log(`error while frontend login ${err}`);
    }
  };
  return (
    <>
      <div className="reg-section">
        <img className="x-img" src={xlogo} alt="" />

        <form className="reg-form" onSubmit={registerClick}>
          <input
            onChange={(e) => {
              setemail(e.target.value);
            }}
            placeholder="email"
            className="reg-input"
            type="email"
          />
          <input
            onChange={(e) => {
              setpassword(e.target.value);
            }}
            placeholder="password"
            className="reg-input"
            type="password"
          />
          <div className="reg-btn-div">
            <Button type="submit" className="reg-btn" variant="success">
              Login ..
            </Button>
            <Button
              onClick={() => {
                navigate("/");
              }}
              className="login-btn"
              variant="danger"
            >
              Register ..
            </Button>
          </div>
        </form>
      </div>
    </>
  );
}

export default Login;
